mapper.select:
select order_code as orderCode, order_active as orderActive, sum_price as sumPrice, puchar_num as pucharNum, pay_time as payTime, o.version as version, a1.area_name as provinceName, a2.area_name as cityName, a3.area_name as areaName, s.address as address, u.user_code as userCode, u.user_name as userName, u.user_phone as phone from t_sys_order o, t_sys_store s, t_sys_user u, t_sys_area a1, t_sys_area a2, t_sys_area a3 where o.is_delete = 0
and order_active = #{orderActive}
and a1.area_code = s.province_code and a2.area_code = s.city_code and a3.area_code = s.area_code and o.store_code = s.store_code and o.store_code = #{storeCode} and u.user_code = o.user_code order by pay_time desc
