mapper.select:
select  count(user_code)
from    t_sys_user
where   is_delete       =   0
and     user_account    =   #{userAccount}
or      user_phone      =   #{phone}

select  count(user_code)
from    t_sys_user
where   is_delete       =   0
and
(
        user_account    =  #{userAccount}
or      user_phone      =   #{phone}
)
and     user_code       !=  #{driverCode}

select driver_code as driverCode, user_name as userName, user_phone as phone, user_id_card as userIdCard, user_account as userAccount, a.version as version from t_sys_user a, t_sys_driver b, t_sys_store s where b.is_delete = 0 and a.user_code = b.driver_code
and driver_code like concat('%', #{driverCode}, '%')
and user_name like concat('%', #{userName}, '%')
and province_code = #{provinceCode}
and city_code = #{cityCode}
and area_code = #{areaCode}
and s.user_code = #{userCode} and b.province_code = s.province_code and b.city_code = s.city_code and b.area_code = s.area_code
