mapper.select:
select order_code as orderCode, sum_price as sumPrice, order_active as orderActive, store_code as storeCode, user_name as userName, user_phone as userPhone, a.version as version, pay_time as payTime from t_sys_order a, t_sys_user b where a.is_delete = 0 and a.user_code = b.user_code
and user_name like concat('%', #{userName}, '%')
and user_phone like concat('%', #{userPhone}, '%')
and order_code like concat('%', #{orderCode}, '%')
and orderActive = #{order_active}
and pay_time >= #{payStartTime}
and pay_time <= #{payEndTime}
order by a.create_time

select order_code as orderCode, sum_price as sumPrice, order_active as orderActive, store_code as storeCode, user_name as userName, user_phone as userPhone, a.version as version, pay_time as payTime from t_sys_order a, t_sys_user b where a.is_delete = 0 and a.user_code = b.user_code and b.store_code = #{userCode}
and user_name like concat('%', #{userName}, '%')
and user_phone like concat('%', #{userPhone}, '%')
and order_code like concat('%', #{orderCode}, '%')
and orderActive = #{order_active}
and pay_time >= #{payStartTime}
and pay_time <= #{payEndTime}
order by a.create_time
